sqlReaderQuery.value:
@if(
    or(
        equals(item().TableName, 'ProfileTimeKeepingWhosWorkings'),
        equals(item().TableName, 'ProfileTimeKeepingWhosWorkings.Archived')
    ),
    concat(
        'SELECT * FROM [', item().TableName, '] WITH (NOLOCK) WHERE YEAR([DateTimeInOut]) = ', item().ArchivalYear, 
        ' AND MONTH([DateTimeInOut]) = ', item().ArchivalMonth, 
        ' AND DAY([DateTimeInOut]) = ', item().ArchivalDay
    ),
    concat(
        'SELECT * FROM [', item().TableName, '] WITH (NOLOCK) WHERE YEAR([CreatedDate]) = ', item().ArchivalYear, 
        ' AND MONTH([CreatedDate]) = ', item().ArchivalMonth, 
        ' AND DAY([CreatedDate]) = ', item().ArchivalDay
    )
)

@if(
    or(
        equals(item().TableName, 'ProfileTimeKeepingWhosWorkings'),
        equals(item().TableName, 'ProfileTimeKeepingWhosWorkings.Archived')
    ),
    concat(
        'SELECT * FROM [', item().TableName, '] WITH (NOLOCK) WHERE YEAR([DateTimeInOut]) = ', item().ArchivalYear, 
        ' AND MONTH([DateTimeInOut]) = ', item().ArchivalMonth, 
        ' AND DAY([DateTimeInOut]) = ', item().ArchivalDay
    ),
    concat(
        'SELECT * FROM [', item().TableName, '] WITH (NOLOCK) WHERE YEAR([CreatedDate]) = ', item().ArchivalYear, 
        ' AND MONTH([CreatedDate]) = ', item().ArchivalMonth, 
        ' AND DAY([CreatedDate]) = ', item().ArchivalDay
    )
)

@if(
    or(
        equals(item().TableName, 'ProfileTimeKeepingWhosWorkings'),
        equals(item().TableName, 'ProfileTimeKeepingWhosWorkings.Archive')
    ),
    concat(
        'SELECT * FROM [', item().TableName, '] WITH (NOLOCK) WHERE YEAR([DateTimeInOut]) = ', variables('Year'), 
        ' AND MONTH([DateTimeInOut]) = ', variables('Month'), 
        if(
            or(
                or(equals(variables('Day'), null), equals(variables('Day'), '')), 
                equals(variables('Day'), 0)
            ), 
            '', 
            concat(' AND DAY([DateTimeInOut]) = ', variables('Day'))
        )
    ),
    concat(
        'SELECT * FROM [', item().TableName, '] WITH (NOLOCK) WHERE YEAR([CreatedDate]) = ', variables('Year'), 
        ' AND MONTH([CreatedDate]) = ', variables('Month'), 
        if(
            or(
                or(equals(variables('Day'), null), equals(variables('Day'), '')), 
                equals(variables('Day'), 0)
            ), 
            '', 
            concat(' AND DAY([CreatedDate]) = ', variables('Day'))
        )
    )
)

FileName.value:
@concat(
    variables('Year'), '-', 
    variables('Month'), 
    if(equals(variables('Day'), ''), '', concat('-', variables('Day'))),
    ' - ', pipeline().RunId, '.parquet'
)
